mapper.select:
select g.*,t.name as typeName from goods g
left join goods_type t on t.id=g.typeId


        g.state = #{state}


        and g.id = #{id}


        and g.name like '%${name}%'


        and g.writer like '%${writer}%'


        and (g.name like '%${keyword}%' or g.writer like '%${keyword}%')


        and g.des like '%${des}%'


        and g.coverImage = #{coverImage}


        and g.price = #{price}


        and g.typeId = #{typeId}


        and g.ishot = #{ishot}
